import React from 'react';
import axios from "axios";
import '../App.css';


export default class CheckOut extends React.Component {

    state = { visitors: [] };

    getVisitors() {
        axios.get('http://127.0.0.1:8000/api/checkins/')
            .then(res => {
                let arr = res.data.filter(elem => elem.checkedIn === true)
                this.setState({ visitors: arr });
            })
    }

    componentDidMount() {
        this.getVisitors();
    }

    getConfirmation() {
        var retVal = confirm("Do you want to continue ?");
        if( retVal == true ) {
           document.write ("User wants to continue!");
           return true;
        } else {
           document.write ("User does not want to continue!");
           return false;
        }
     }

    checkOut(obj) {
        // if clicked => mark checkedIn as false and set timeOut to current time
        var today = new Date();
        var time = today.getHours() + ":" + today.getMinutes();

        const item = {
            name: obj.name,
            PID: obj.PID,
            date: obj.date,
            timeIn: obj.timeIn,
            timeOut: time,
            reason: obj.reason,
            checkedIn: false,
            staff: obj.staff
        };

        axios.put(`http://127.0.0.1:8000/api/checkins/${obj.id}/`, item)

        // dialog box
        this.getConfirmation()

        this.componentDidMount()
        window.location.reload();
    }

    render() {
        let { visitors } = this.state;

        return (
            <div class="checkout">
                <h2>Check Out</h2>
                <div>
                    <section>
                        {visitors.map((data, index) => (
                            <button key={index} onClick={() => this.checkOut(data)}>
                                <p>{data.name}</p>
                                <p>{data.PID}</p>
                            </button>
                        ))}
                    </section>
                </div>
            </div >
        );
    }
}
