import React from 'react';
import axios from "axios";
import '../App.css';
import { confirmAlert } from 'react-confirm-alert'; // Import
import 'react-confirm-alert/src/react-confirm-alert.css'; // Import css


export default class CheckOut extends React.Component {

    state = { visitors: [] };

    getVisitors() {
        axios.get('http://127.0.0.1:8000/api/checkins/')
            .then(res => {
                let arr = res.data.filter(elem => elem.checkedIn === true)
                this.setState({ visitors: arr });
            })
    }

    componentDidMount() {
        this.getVisitors();
    }


    submit = (obj) => {
        confirmAlert({
          title: 'Confirm to submit',
          message: 'Are you sure to do this.',
          buttons: [
            {
              label: 'Yes',
              onClick: () => this.checkOut(obj)
            },
            {
              label: 'No',
              onClick: () => alert('Click No')
            }
          ]
        });
      };

    checkOut(obj) {

        // if clicked => mark checkedIn as false and set timeOut to current time
        var today = new Date();
        var time = today.getHours() + ":" + today.getMinutes();

        const item = {
            name: obj.name,
            PID: obj.PID,
            date: obj.date,
            timeIn: obj.timeIn,
            timeOut: time,
            reason: obj.reason,
            checkedIn: false,
            staff: obj.staff
        };

        axios.put(`http://127.0.0.1:8000/api/checkins/${obj.id}/`, item)

        this.componentDidMount()
        window.location.reload();
    }

    render() {
        let { visitors } = this.state;

        return (
            <div class="checkout">
                <h2>Check Out</h2>
                <div>
                    <section>
                        {visitors.map((data, index) => (
                            <button key={index} onClick={() => this.submit(data)}>
                                <p>{data.name}</p>
                                <p>{data.PID}</p>
                            </button>
                        ))}
                    </section>
                </div>
            </div >
        );
    }
}
